mapper.insert:
  		insert
		 into
		tbl_board
		(
			board_no	
		 ,	board_title
		 ,  board_content
		 ,	board_writer
		 ,	board_count
		 ,	board_regdate
		)
		values
		(
			#{board_no}
		,	#{board_title}
		,   #{board_content}
		,	#{board_writer}
		,	#{board_count}
		,	CURRENT_TIMESTAMP()
		)

mapper.update:
update tbl_board set
board_count = board_count+1
where board_no = #{board_no}

