mapper.insert:
INSERT INTO tbl_item
(
	ITEM_NO
,	ITEM_NAME
,	ITEM_PRICE
,	ITEM_SIZE
,	ITEM_COLOR
,	ITEM_CONTENT
,	ITEM_CATEMAIN
,	ITEM_CATESUB
,	ITEM_SUBCONTENT
,	ITEM_MODEL
)
VALUES
(
	#{item_no}
,	#{item_name}
,	#{item_price}
,	#{item_size}
,	#{item_color}
,	#{item_content}
,	#{item_catemain}
,	#{item_catesub}
,	#{item_subcontent}
,	#{item_model}
)

INSERT INTO TBL_OPTION (OPTION_NO, OPTION_CONTENT, OPTION_ITEM_NO , OPTION_VOL 
)VALUES(
(SELECT NVL(MAX(OPTION_NO), 0) + 1 FROM TBL_OPTION) , #{item_option} , #{item_no} , #{item_vol}
)

insert into TBL_IMAGE (IMG_NO , IMG_ORGNAME , IMG_STRNAME , IMG_SIZE , IMG_ITEM_NO
)VALUES(
(SELECT NVL(MAX(IMG_NO), 0) + 1 FROM TBL_IMAGE), #{IMG_ORGNAME}, #{IMG_STRNAME} , #{IMG_SIZE} , #{ITEM_NO}
)
