mapper.insert:
  		insert
		 into
		tbl_member
		(
			me_id		
		 ,	me_email
		 ,  me_name
		 ,	me_pwd
		 ,	me_regdate
		)
		values
		(
			#{me_id}
		,	#{me_email}
		,   #{me_name}
		,	#{me_pwd}
		,	now()
		)

mapper.select:
select 
 count(*)
from 
 tbl_member
where me_email = #{me_email}
and   me_pwd   = #{me_pwd}

select 
   *
from
tbl_member
where me_email = #{me_email} 
and me_pwd = #{me_pwd}

mapper.update:
update tbl_member
set
me_pwd = #{me_pwd} 
where me_id = #{me_id}

