mapper.insert:
  		insert
		 into
		tbl_member
		(
			me_id		
		 ,	me_email
		 ,  me_name
		 ,	me_pwd
		 ,	me_regdate
		)
		values
		(
			#{me_id}
		,	#{me_email}
		,   #{me_name}
		,	#{me_pwd}
		,	now()
		)

mapper.select:
select 
 count(*)
from 
 tbl_member
where me_email = #{me_email}
and   me_pwd   = #{me_pwd}

select 
   *
from
tbl_member
where me_email = #{me_email} 
and me_pwd = #{me_pwd}

mapper.update:
UPDATE
	tbl_member
SET
	me_name = #{me_name}
,	me_id = #{me_id}	
,	me_pwd = #{me_pwd}
,	me_email = #{me_email}
where
	me_id = #{me_id}

UPDATE
	tbl_member
SET
	me_name = #{me_name}
,	me_id = #{me_id}
,	me_email = #{me_email}
where
	me_id = #{me_id}
