mapper.insert:
  		insert
		 into
		tbl_notice
		(
			notice_no	
		 ,	notice_title
		 ,  notice_content
		 ,	notice_writer
		 ,	notice_count
		 ,	notice_regdate
		 ,  notice_updateDate
		)
		values
		(
			#{notice_no}
		,	#{notice_title}
		,   #{notice_content}
		,	#{notice_writer}
		,	#{notice_count}
		,	#{notice_regdate}
		,	#{notice_updateDate}
		)

mapper.update:
update tbl_notice
	set notice_title = #{notice_title},	
		notice_content = #{notice_content}
		notice_updateDate = #{notice_updateDate}
where notice_no = #{notice_no}

update tbl_notice set
notice_count = notice_count+1
where notice_no = #{notice_no}
